mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PASSWORD = #{memberPassword}
WHERE ID = #{id}

UPDATE TBL_PET
SET PET_NAME = #{petName}, PET_GENDER = #{petGender}, PET_BREED = #{petBreed}, PET_BIRTH = #{petBirth}, PET_WEIGHT = #{petWeight}, PET_NEUTER = #{petNeuter}, PET_VET = #{petVet}
WHERE ID = #{id}
